import PropTypes from 'prop-types';
import React, { Component } from 'react';

import { Accordion, ButtonToolbar, Button, Form, Col, Alert } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import NumInput from './NumInput.js';
import DateInput from './DateInput.js';
import Toast from './Toast.js';

export default class IssueEdit extends Component {

  constructor() {
    super();
    this.state = {
      issue: {
        _id: '', title: '', status: '', owner: '', effort: null,
        completionDate: null, created: null,
      },
      invalidFields: {}, showingValidation: false,
      toastVisible: false, toastMessage: '', toastType: 'success',
    };
    this.onChange = this.onChange.bind(this);
    this.onValidityChange = this.onValidityChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.dismissValidation = this.dismissValidation.bind(this);
    this.showValidation = this.showValidation.bind(this);
    this.showSuccess = this.showSuccess.bind(this);
    this.showError = this.showError.bind(this);
    this.dismissToast = this.dismissToast.bind(this);
    this.onKeyPress = this.onKeyPress.bind(this);
  }

  componentDidMount() {
    this.loadData();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.params.id !== this.props.params.id) {
      this.loadData();
    }
  }

  onChange(event, convertedValue) {
    console.log('==> onChange dans IssueEdit');

    const issue = Object.assign({}, this.state.issue);
    // On utilise le nom de chaque input comme une clé dans l'objet state
    // afin d'affecter la nouvelle valeur de la propriété modifiée.
    // Cette technique permet de combiner tous onChange de chaque input
    // dans une seule méthode.
    const value = (convertedValue !== undefined) ? convertedValue : event.target.value;
    issue[event.target.name] = value;
    this.setState({ issue });
  }

  onValidityChange(event, valid) {
    console.log('==> onValidityChange dans IssueEdit');
    const invalidFields = Object.assign({}, this.state.invalidFields);
    if (!valid) {
      invalidFields[event.target.name] = true;
    } else {
      delete invalidFields[event.target.name];
    }
    this.setState({ invalidFields });
  }

  /**
   * Mise à jour d'une tâche
   *
   * @param  {[type]} event [description]
   * @return {[type]}       [description]
   */
  onSubmit(event) {
    console.log('==> Soumission de la mise à jour : ');

    event.preventDefault();
    this.showValidation();
    if (Object.keys(this.state.invalidFields).length !== 0) {
      return;
    }
    fetch(`/api/issues/${this.props.params.id}`, {
      method: 'PUT',
      headers: { 
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(this.state.issue),
    }).then(response => {
      if (response.ok) {
        response.json().then(updatedIssue => {
          updatedIssue.created = new Date(updatedIssue.created);
          if (updatedIssue.completionDate) {
            updatedIssue.completionDate = new Date(updatedIssue.completionDate);
          }
          this.setState({ issue: updatedIssue });
          this.showSuccess('Updated issue successfully.');
        });
      } else {
        response.json().then(error => {
          this.showError(`Failed to update issue: ${error.message}`);
        });
      }
    }).catch(err => {
      this.showError(`Error in sending data to server: ${err.message}`);
    });
  }

  showValidation() {
    this.setState({ showingValidation: true });
  }

  dismissValidation() {
    this.setState({ showingValidation: false });
  }

  loadData() {
    fetch(`/api/issues/${this.props.params.id}`).then(response => {
      if (response.ok) {
        response.json().then(issue => {
          issue.created = new Date(issue.created);
          issue.completionDate = issue.completionDate != null ?
            new Date(issue.completionDate) : null;
          this.setState({ issue });
        });
      } else {
        response.json().then(error => {
          this.showError(`Failed to fetch issue: ${error.message}`);
        });
      }
    }).catch(err => {
      this.showError(`Error in fetching data from server: ${err.message}`);
    });
  }

  showSuccess(message) {
    this.setState({ toastVisible: true, toastMessage: message, toastType: 'success' });
  }

  showError(message) {
    this.setState({ toastVisible: true, toastMessage: message, toastType: 'danger' });
  }

  dismissToast() {
    this.setState({ toastVisible: false });
  }

  onKeyPress(event) {
    // Object.keys(this.state.invalidFields).length !== 0
    if (event.key === "Enter") {
      console.log(this.state.invalidFields);
      event.preventDefault();
    }
  }

  render() {
    const issue = this.state.issue;
    /*const validationMessage = Object.keys(this.state.invalidFields).length === 0 ? null
      : (<div className="error">Please correct invalid fields before submitting.</div>);*/
    let validationMessage = null;
    if (Object.keys(this.state.invalidFields).length !== 0 && this.state.showingValidation) {
      validationMessage = (
        <Alert bsStyle="danger" onDismiss={this.dismissValidation}>
          Please correct invalid fields before submitting.
        </Alert>
      );
    }

    return (
      <Accordion  header="Edit Issue">
        <Form horizontal onSubmit={this.onSubmit} onKeyPress={this.onKeyPress}>
          <Form.Group>
            <Col componentClass={Form.Label} sm={3}>Created</Col>
            <Col sm={9}>
              <Form.Control.Static>{issue.created ? issue.created.toDateString() : ''}</Form.Control.Static>
            </Col>
          </Form.Group>
          <Form.Group>
            <Col componentClass={Form.Label} sm={3}>Status</Col>
            <Col sm={9}>
              <Form.Control componentClass="select" name="status" value={issue.status} onChange={this.onChange}>
                <option value="NEW">New</option>
                <option value="OPEN">Open</option>
                <option value="ASSIGNED">Assigned</option>
                <option value="IN_PROGRESS">In Progress</option>
                <option value="DONE">Done</option>
              </Form.Control>
            </Col>
          </Form.Group>
          <Form.Group>
            <Col componentClass={Form.Label} sm={3}>Owner</Col>
            <Col sm={9}>
              <Form.Control name="owner" value={issue.owner} onChange={this.onChange} />
            </Col>
          </Form.Group>
          <Form.Group>
            <Col componentClass={Form.Label} sm={3}>Effort</Col>
            <Col sm={9}>
              <Form.Control
                componentClass={NumInput} name="effort"
                value={issue.effort} onChange={this.onChange}
              />
            </Col>
          </Form.Group>
          <Form.Group validationState={this.state.invalidFields.completionDate ? 'error' : null}>
            <Col componentClass={Form.Label} sm={3}>Completion Date</Col>
            <Col sm={9}>
              <Form.Control
                componentClass={DateInput} name="completionDate"
                value={issue.completionDate} onChange={this.onChange}
                onValidityChange={this.onValidityChange}
              />
              <Form.Control.Feedback />

            </Col>
          </Form.Group>
          <Form.Group>
            <Col componentClass={Form.Label} sm={3}>Title</Col>
            <Col sm={9}>
              <Form.Control name="title" value={issue.title} onChange={this.onChange} />
            </Col>
          </Form.Group>
          <Form.Group>
            <Col smOffset={3} sm={6}>
              <ButtonToolbar>
                <Button bsStyle="primary" type="submit">Submit</Button>
                <LinkContainer to="/issues">
                  <Button bsStyle="link">Back</Button>
                </LinkContainer>
              </ButtonToolbar>
            </Col>
          </Form.Group>
          <Form.Group>
            <Col smOffset={3} sm={9}>{validationMessage}</Col>
          </Form.Group>
        </Form>
        <Toast showing={this.state.toastVisible}
            message={this.state.toastMessage}
            onDismiss={this.dismissToast} bsStyle={this.state.toastType}
            />
      </Accordion >
    );
  }
}

IssueEdit.propTypes = {
  params: PropTypes.object.isRequired,
};
